import styled from "styled-components";

export const BorderlessInput = styled.input`
    border: none;
  background-color: transparent;
  outline: none;
  width: 100%;
  font-size: 16px;
  font-family: Inter;
`;
export const Row = styled.div`
  display: flex;
  gap: 10px;
`;
export const RowSpaceBetween = styled.div`
  display: flex;
  gap: 10px;
  justify-content: space-between;
  align-items: center;
  padding: auto 20px;
`;

interface SizeProps {
  width?: string;
  height?: string;
}

export const ImageContainer = styled.img<SizeProps>`
  width: ${({ width }) => width || "auto"};
  height: ${({ height }) => height || "auto"};
`;
export const SpaceHolder = styled.div<SizeProps>`
  width: ${({ width }) => width || "auto"};
  height: ${({ height }) => height || "auto"};
`;

interface TextProps {
    fontSize?: string;
    color?: string;
    fontWeight?: string;
  }

  export const TextWrapper = styled.span<TextProps>`
  font-size: ${({ fontSize }) => fontSize || "14px"};
  color: ${({ color }) => color || "rgba(0, 0, 0, 1)"};
  font-weight: ${({ height }) => height || "auto"};
`;
