import styled from "styled-components";
import {
  Column,
  Divider,
  ImageContainer,
  Row,
  RowSpaceBetween,
  TextWrapper,
} from "../styled";
//@ts-ignore
import Inspiration from "../assets/svg/sidenav/inspiration.svg";
//@ts-ignore
import Templates from "../assets/svg/sidenav/templates.svg";
//@ts-ignore
import Collections from "../assets/svg/sidenav/collection.svg";
//@ts-ignore
import Brands from "../assets/svg/sidenav/brands.svg";
//@ts-ignore
import Chevron from "../assets/svg/sidenav/chevron.svg";
import { useState } from "react";

const SideBar = () => {
  const [opened, setOpened] = useState<boolean>(true);

  const sideNavNames: Array<{ navName: string; navImageUrl: string }> = [
    {
      navName: "Inspiration",
      navImageUrl: Inspiration,
    },
    {
      navName: "Templates",
      navImageUrl: Templates,
    },
    {
      navName: "Collections",
      navImageUrl: Collections,
    },
    { navName: "Brands", navImageUrl: Brands },
  ];

  return (
    <SideBarWrapper opened={opened}>
      {sideNavNames.map(
        (nav: { navName: string; navImageUrl: string }, index: number) => (
          <RowSpaceBetween key={index}>
            {opened && <SideNav>
              <ImageContainer
                src={nav.navImageUrl}
                width="18px"
                height="18px"
              />
              <TextWrapper fontWeight="600">{nav.navName}</TextWrapper>
            </SideNav>}
            {index === 0 && (
              <ImageContainer
                src={Chevron}
                width="12px"
                height="20px"
                style={{
                  transform: opened ? 'rotate(0deg)' : 'rotate(180deg)',
                  transition: 'transform 0.3s ease'
                }}
                onClick={() => setOpened(!opened)}
              />
            )}
          </RowSpaceBetween>
        )
      )}
      {opened && <Column>
      <Divider/>
      <TextWrapper color="rgba(127, 127, 127, 1)" fontSize="11px" fontWeight="600">Featured</TextWrapper>



      </Column>}
    </SideBarWrapper>
  );
};

export default SideBar;

const SideBarWrapper = styled(Column)<{ opened: boolean }>`
  background: rgba(245, 245, 245, 0.5);
  max-width: ${({ opened }) => (opened ? '200px' : '15px')};
  border-right: 1px solid rgba(180, 180, 180, 1);
  width:100%;
  height: calc(100vh - 80px);
  padding: 15px;
  overflow: hidden;
  transition: all 0.3s ease;
`;

const SideNav = styled(Row)`
  padding: 12px 15px;
  border-radius: 6px;
  &:hover {
    border: 1px solid #a259ff;
    cursor: pointer;
  }
`;
