import React from 'react';

export const BorderlessInput = () => (
  <input className="border-none bg-transparent outline-none w-full text-base font-inter" />
);

export const Row = ({ children }: {children: any}) => (
  <div className="flex gap-2.5 w-full">
    {children}
  </div>
);

export const RowSpaceBetween = ({ children }: {children: any}) => (
  <div className="flex gap-2.5 justify-between items-center w-full px-5">
    {children}
  </div>
);

export const Column = ({ children }: {children: any}) => (
  <div className="flex flex-col gap-2.5 w-auto">
    {children}
  </div>
);

export const ColumnSpaceBetween = ({ children }: {children: any}) => (
  <div className="flex flex-col gap-2.5 justify-between items-start w-auto px-5">
    {children}
  </div>
);

export const ImageContainer = ({ width = "auto", height = "auto", src = "", style }) => (
  <img style={{ width, height }} src={src}/>
);

export const SpaceHolder = ({ width = "auto", height = "auto" }) => (
  <div style={{ width, height }} />
);

export const TextWrapper = ({ fontSize = "14px", color = "rgba(0, 0, 0, 1)", fontWeight = "400", children }:{fontSize?: string, color?: string, fontWeight?: string, children: any}) => (
  <span style={{ fontSize, color, fontWeight }} className="font-inter">
    {children}
  </span>
);

export const Divider = ({ height = "30px", color = "rgba(180, 180, 180, 1)" }) => (
  <div className={`w-full bg-[${color}] my-[${height}]`} style={{ height: "1px" }} />
);
